import React,{useEffect} from 'react'
import { connect } from 'react-redux';
import axios from 'axios'
import store from '../../Controllers/Store/store'
import * as actions from '../../Controllers/Actions/Actions'
import TopNav from './TopNav'

function Home(){
    let userName = "";

    useEffect(() => {
        document.title = "Home | Friend Finder";
        console.log(localStorage.getItem('key'))
        axios.post('http://localhost:4000/check-login',[localStorage.getItem('key')])
        .then(res=>{
            console.log(res.data)
            store.dispatch(actions.addUserName(res.data))

        })
        .catch(err=>{
            alert(err)
        })
        setTimeout(() => {
            readName()
        }, 1000);
    }, [])
    function readName(){
        try{
            userName = store.getState().setName[0].name
        }
        catch(err){
            console.log(err.message);
            userName = "loading.."
        }
    }
    return(
        <div className="home">
            <TopNav name={userName} />
            <div className="main-content">
                <div className="home-people-you-may-know">
                    <p>People you may know</p>
                </div>
            </div>
        </div>
    )
}

export default connect(
    null,
    { actions }
)(Home);
