import React,{useState,useCallback} from 'react'
import Button from '../Button'
import img from './profile3.jpeg'
import { compose } from 'redux';

function FriendRecomendation(props){
    const [toggleButton,setToggleButton] = useState({
        userId:""
    });
    function handleClick(event){
        setToggleButton(prev=>{
            prev.userId = event.target.name;
            prev.view = false
            return prev
        })
        event.target.disabled = true
        props.method(toggleButton.userId)
    }
    return(
        <div className="people-you-may-know-friends-div">
            <img className="friend-profile-image" src={img} alt="propfile image"/>
            <div>
                <p className="friend-name">{props.name}</p>
                <p className="mutual-friends"><span>{props.mutual}</span> mutual friends</p>
                <button name={props.id} onClick={handleClick} className="friends-button">Add friend</button><Button class="friends-button-2" name="Remove"/>
            </div>
        </div>
    )
}

export default FriendRecomendation
