import React,{useState} from 'react'
import InputWrapper from './InputWrapper'
import Button from '../Button'
import axios from 'axios'
import { createStore } from 'redux'
import {useHistory} from 'react-router-dom'
import loginApp from '../../Controllers/Reducer/reducer'
import * as actions from '../../Controllers/Actions/Actions'

function Header(){
    const history = useHistory()
    const store = createStore(loginApp,window.__REDUX_DEVTOOLS_EXTENSION__ && window.__REDUX_DEVTOOLS_EXTENSION__())
    const storeName = createStore(setName,window.__REDUX_DEVTOOLS_EXTENSION__ && window.__REDUX_DEVTOOLS_EXTENSION__())
    const [email , setEmail] = useState('')
    const [password , setPassword] = useState('')

    const unsubcribe = store.subscribe(() => console.log(store.getState()))

    function saveData(){
        store.dispatch(actions.addLoginDetails(email,password))

        axios.post(`http://localhost:4000/log-in`,store.getState())
        .then(res=>{
            if(res.data.status === "login sucess"){
                localStorage.setItem("key",res.data.token)
                storeName.dispatch(actions.addUserName(res.data.name))
                // history.push("/home")
            }
        })
        .catch(err=>{
            alert(err)
        })
    }
    // unsubcribe()
    return(
        <div className="top-nav">  
            <div className="top-nav-margin"> 
                <div className="top-nav-left">
                    <h1>Friend Finder</h1>
                </div>
                <div className="top-nav-right">
                    <InputWrapper method={setEmail} class="header-input" text="Email or Phone" type="text"/>
                    <InputWrapper method={setPassword} class="header-input" text="Password" type="password"/>
                    <Button method={saveData} class="ff-button" name="Log In"/>
                </div>
            </div>
        </div>      
    );
}

export default Header
