import React,{useEffect,useCallback,useState} from 'react'
import { connect } from 'react-redux';
import axios from 'axios'
import {useHistory} from 'react-router-dom'
import store from '../../Controllers/Store/store'
import * as actions from '../../Controllers/Actions/Actions'
import TopNav from './TopNav'

function Home(){
    const history = useHistory()
    const [name,setName] = useState("loading..");
    useEffect(() => {
        var isLoad = true;
        async function loadData (){
            document.title = "Home | Friend Finder";
            await axios.post('http://localhost:4000/log-in/check-login',[localStorage.getItem('key')])
            .then(res => {
                if(res.data === "jwt expired"){
                    localStorage.clear()
                    history.push('/');
                }
                if(res.data === "jwt must be provided") {
                    localStorage.clear()
                    history.push('/');
                }
                if(res.data === "Cannot read property 'toString' of null"){
                    localStorage.clear()
                    history.push('/');
                }
                store.dispatch(actions.addUserName(res.data))
                try{
                    if (isLoad) setName(store.getState().setName[0].name)
                }
                catch(err){
                    console.log(err.message);
                }
            })
            .catch( err => alert(err) ) 
        }
        loadData()

        return( () => isLoad = false )
    }, [])

    const names = useCallback(
        () => { return name },
    [name],)

    return(
        <div className="home">
            <TopNav name={names()} />            
            <div className="home-main-content">
                <div className="home-people-you-may-know">
                    <p>People you may know</p>
                </div>
            </div>
        </div>
    )
}

export default connect(
    null,
    { actions }
)(Home);
