function loginApp(state = [], action) {
  switch (action.type) {
    case "LOGIN_AUTH":
      return [
        {
          email: action.loginDetails.email,
          password: action.loginDetails.password
        }
      ]
    default:
      return state
  }
}

export default loginApp
