import React,{useState,useCallback} from 'react'
import Header from './Header'
import FriendImg from '../Assets/Images/Connect_with_friends.png'
import Input from '../Input'
import Button from '../Button'
import { createStore } from 'redux'
import loginApp from '../../Controllers/Reducer/reducer'
import * as actions from '../../Controllers/Actions/Actions'

function SignIn(){
    const store = createStore(loginApp,+  window.__REDUX_DEVTOOLS_EXTENSION__ && window.__REDUX_DEVTOOLS_EXTENSION__())
    const [error,setError] = useState('')
    const renderError = useCallback(
        () => {
            return(
                <p className={error===''?"":"alert"}>{error}</p>
            )
        },
        [error],
    )
    function callError(){
        //catch the password not match error
        setError("Passwords do not match")
    }
    const [userDetails , setUserDetails] = useState({
        name:"",
        userName:"",
        email:"",
        password:"",
        birthday:"",
        gender:""
    })
    function sendData(){
        store.dispatch(actions.addLoginDetails(userDetails.name.userDetails.userName,userDetails.email,userDetails.password,userDetails.birthday,userDetails.gender))
    }
    function setName(event){
        setUserDetails(prev=>{
            prev.name=event.target.value;
        })
    }
    return(
        <div>
            <Header />  
            <div className="main-content">
                <div className="main-content-left">
                    <h3>Friend Finder helps you connect and share with the people in your life.</h3>
                    <img src={FriendImg} />
                </div>
                <div className="main-content-right">
                    <h1>Create an account</h1>
                    <p>It's quick and easy.</p>
                    {renderError()}
                    <div className="main-content-form">
                        <div className="flex-wrap">
                            <Input method={setName} class="input-form" text="Full Name" />
                            <Input class="input-form width" text="User name" />
                        </div>
                        <Input class="input-form" text="Email address" />
                        <Input class="input-form" text="New Password" />
                        <Input class="input-form" text="Re enter new Password" />
                        <div className="form-option-list">
                            <p>Birthday</p>
                            <input className="ff-date" type="date"/>
                        </div>
                        <div className="form-option-list">
                            <p>Gender</p>
                            <input type="radio" name="gender" value="Male"/><span>Male</span>
                            <input type="radio" name="gender" value="Female"/><span>Female</span>
                            <input type="radio"  name="gender"value="Other"/><span>Other</span>
                        </div>
                        <Button method={sendData} class="ff-sign-up-button" name="Sign Up"/>
                    </div>
                </div>
            </div>     
        </div>
    );
}

export default SignIn
