import React,{useState} from 'react'
import img from './profile3.jpeg'
import HiddenFriendRequestNotifications from './HiddenFriendRequestNotifications'
function TopNav(props){
    const [display,setDisplay] = useState({
        settings:false,
        friendRequest:false
    })
    function handleToggle(event){
        console.log(event.target.name)
        setDisplay(prevDisplay=>{
            if(event.target.name === "settings"){
                prevDisplay[event.target.name] = !prevDisplay[event.target.name]
                prevDisplay.friendRequest = false
            }
            else if(event.target.name === "friendRequest"){
                prevDisplay[event.target.name] = !prevDisplay[event.target.name]
                prevDisplay.settings = false
            }
            return prevDisplay
        })
    }
    function custumRenderHiddenDiv(){
        if(display.settings){
            return (
                <div className="hidden-menu settings">
                    <span className="beacon"></span>
                    <div className="hidden-menu-inside">
                        <p>logout</p>
                    </div>
                </div>
            )
        }
        else if(display.friendRequest){
            return <HiddenFriendRequestNotifications/>
        }
    }
    return(
        <div className="home-top-nav">
            <div className="top-nav-margin"> 
                <h1>f</h1>
                <div className="top-nav-right">
                    <img className="profile-img" src={img} alt="current user profile."/>
                    <p className="profile-name">{props.name}</p>
                    <ul className="home-top-nav-list">
                        <li><i className="fa fa-bell"></i></li>
                        <button name="friendRequest" onClick={handleToggle}><i className="fa fa-users"></i></button>
                        <li><i className="fa fa-envelope"></i></li>
                        <li onClick={handleToggle} ><i className="fa fa-caret-down"></i></li>
                    </ul>
                </div>
            </div>
            {custumRenderHiddenDiv()}
        </div>
    )
}

export default TopNav
