import React,{useState} from 'react'
import InputWrapper from './InputWrapper'
import Button from '../Button'
import { createStore } from 'redux'
import loginApp from '../../Controllers/reducer'

function SignIn(props){
    const store = createStore(loginApp)
    const [loginDetails , setLoginDetails] = useState({
        email:"",
        password:""
    })
    function setEmail(event){
        setLoginDetails(prevState=>{
            prevState.email=event.target.value
        })
    }
    function setPassword(event){
        setLoginDetails(prevState=>{
            prevState.password=event.target.value
        })
    }
    function addLoginDetails(){
        return {type:"LOGIN_AUTH" , loginDetails:[loginDetails.email,loginDetails.password]}
    }
    return(
        <div className="top-nav">  
            <div className="top-nav-margin"> 
                <div className="top-nav-left">
                    <h1>Friend Finder</h1>
                </div>
                <div className="top-nav-right">
                    <InputWrapper value={loginDetails.email} method={setEmail} class="header-input" text="Email or Phone" type="text"/>
                    <InputWrapper value={loginDetails.password} method={setPassword} class="header-input" text="Password" type="password"/>
                    <Button method={addLoginDetails} class="ff-button" name="Log In"/>
                </div>
            </div>
        </div>      
    );
}

export default SignIn
