import React,{useState} from 'react'
import InputWrapper from './InputWrapper'
import Button from '../Button'
import { createStore } from 'redux'
import loginApp from '../../Controllers/reducer'

function Header(){
    const store = createStore(loginApp)
    const [email , setEmail] = useState('')
    const [password , setPassword] = useState('')

    function setEmailAddress(event){
        setEmail(prevState=>{
            prevState.email=event.target.value
        })
    }
    function setPasscode(event){
        setPassword(prevState=>{
            prevState.password=event.target.value
        })
    }
    function addLoginDetails(){
        return {type:"LOGIN_AUTH" , loginDetails:[email,password]}
    }
    return(
        <div className="top-nav">  
            <div className="top-nav-margin"> 
                <div className="top-nav-left">
                    <h1>Friend Finder</h1>
                </div>
                <div className="top-nav-right">
                    <InputWrapper method={setEmailAddress} class="header-input" text="Email or Phone" type="text"/>
                    <InputWrapper method={setPasscode} class="header-input" text="Password" type="password"/>
                    <Button method={addLoginDetails} class="ff-button" name="Log In"/>
                </div>
            </div>
        </div>      
    );
}

export default Header
