import React,{useEffect,useCallback,useState} from 'react'
import { connect } from 'react-redux';
import axios from 'axios'
import {useHistory} from 'react-router-dom'
import store from '../../Controllers/Store/store'
import * as actions from '../../Controllers/Actions/Actions'
import TopNav from './TopNav'
import FriendRecomendation from './FriendRecomendation';

function Home(){
    const history = useHistory()
    const [name,setName] = useState("");
    useEffect(() => {
        var isLoad = true;
        async function loadData (){
            document.title = "Home | Friend Finder";
            await axios.post('http://localhost:4000/log-in/check-login',[localStorage.getItem('key')])
            .then(res => {
                if(res.data.message === "jwt expired"){
                    localStorage.clear()
                    history.push('/');
                }
                if(res.data.message === "jwt must be provided") {
                    localStorage.clear()
                    history.push('/');
                }
                if(res.data.message === "Cannot read property 'toString' of null"){
                    localStorage.clear()
                    history.push('/');
                }
                store.dispatch(actions.addUserName(res.data.name))
                store.dispatch(actions.addAllRecommendedFriends(res.data.userData))
                try{
                    if (isLoad) setName(store.getState().setName[0].name)
                }
                catch(err){
                    console.log(err.message);
                }
            })
            .catch( err => alert(err) ) 
        }
        loadData()

        return( () => isLoad = false )
    }, [])
    // const unsubcribe = store.subscribe(() => console.log(store.getState()))

    const names = useCallback(
        () => { return name },
    [name],)

    const [toggleButton,setToggleButton] = useState({
        name:"",
        view:true
    });
    function sendFriendRequest(event){
        let details = {}
        setToggleButton(prev=>{
            prev.name = event.target.name;
            prev.view = false
        })
        details = {
            requestFrom:name,
            requestTo:event.target.name
        }
        // axios.post('',)
        console.log(details)
    }

    var friendsAvailable;
    try{
        friendsAvailable = store.getState().setFriends[0].details.map(items=>{
            return <FriendRecomendation state={toggleButton}  key={items.id} name={items.userName} mutual="-" method={sendFriendRequest} id={items.id}/>
        })
    }
    catch(err){
        console.log("error at 69:",err.message)
    }
    return(
        <div className="home">
            <TopNav name={names()} />            
            <div className="home-main-content">
                <div className="home-people-you-may-know">
                    <p>People you may know</p>
                    <div className="people-you-may-know-friends">
                        {friendsAvailable}
                    </div>
                </div>
            </div>
        </div>
    )
}

export default connect(
    null,
    { actions }
)(Home);
