import React,{useState} from 'react'
import {useHistory} from 'react-router-dom'
import axios from 'axios'
import Header from './Components/Header/Header'
import MenuNavActivity from './Components/Menu-Navigation/MenuNavActivity'
import Dashboard from './Components/Dashboard/Dashboard'
import Promotions from './Components/Promotions/Promotions'

function Home(){

    const history = useHistory();
    const[loginAuth,setLoginAuth] = useState("");
    const[userData,setUserData] = useState("");
    React.useEffect(()=>{
        document.title = "Dashboard · Splitwise"
        const token = localStorage.getItem("login-key");
        // console.log(token)

        async function fetchData() {
            // You can await here
            // const response = await MyAPI.getData(someId);
            // ...
            const res = await axios.post(`http://localhost:4000/check-login`,{"data":token})
            //console.log(res);
            console.log(res.data);
            setLoginAuth(res.data[0])
            setUserData(res.data[1])
        }
        fetchData(); // Or [] if effect doesn't need props or state

        if(loginAuth == "jwt expired") {
            localStorage.clear()
            history.push("/loading")
            setTimeout(()=>{
                history.push("/");
            },1700)
        }
        if(loginAuth == "jwt must be provided") {
            localStorage.clear()
            history.push("/loading")
            setTimeout(()=>{
                history.push("/");
            },1700)
        }
    },[])
    console.log(userData[0])
    return(
        <div>
            <Header name={userData[0]}/>
            <div className="main-content">
                <MenuNavActivity />
                <Dashboard />
                <Promotions />
            </div>
        </div>
      )
}

export default Home
