import React,{useState} from 'react'
import SummaryHeader from './SummaryHeader'
import SummaryList from './SummaryList/SummaryList'
import SummaryChart from './SummaryChart/SummaryChart'


function Summary(props){
    const [toggle,setToggle] = useState([{
        list:true,
        chart:false
    }])

    function handeToggle(newToggle){
        setToggle(newToggle)
    }
    const summaryOwn = props.userdata.map(item=>{
        return <SummaryOwnContent aClass="sumary-you-own-inside-p" url={ProfileImg2} amount={(item.amount)/2} name={item.name}/>
    })
    return(
        <div className="summary">
            <SummaryHeader viewToggle={toggle} handTog={handeToggle} userData={props.userExpenses}/>
            <SummaryList show={toggle[0]} userData={props.userExpenses}/>
            <SummaryChart show={toggle[0]} userData={props.userExpenses}/>
            {summaryOwn}
        </div>
    )
}

export default Summary
