import React,{ useState, useEffect } from 'react'
import axios from 'axios'
import ExpTrackerBlock from './ExpTrackerBlock';
import Summary from "../Summary/Summary";


function DashboardExpenseTracker(props){
    const[expenses,setExpenses] = useState([{
        name:"",
        description: "",
        amount: "",
        time:""
    }]);

    useEffect(() => {
        const token = localStorage.getItem("login-key");

        axios.post(`http://localhost:4000/expense-view`,{"data":token})
        .then(res=>{
            console.log(res.data)
            setExpenses(prevExpenses=>{
                return res.data
            })
        })
        return () => {

        };
    }, []);
    function updateAmount(){
        var amount=0;
        expenses.map(item=>{
            parseFloat(amount+=parseFloat(item.amount/2))
        })
        return amount
    }
    return(
        <div>
            <div className="dashboard-exp-track-header">
                <ExpTrackerBlock class="dashboard-exp-track-content" pClass="exp-track-p" expType="total balance" amount="+ $28.50" />
                <ExpTrackerBlock class="dashboard-exp-track-content-center" pClass="exp-track-p-2" expType="you owe" amount="$33.00" />
                <ExpTrackerBlock class="dashboard-exp-track-content" pClass="exp-track-p" expType="you are owed" amount={updateAmount()} />
            </div>
            <Summary  userExpenses={expenses}/>
        </div>
    )
}

export default DashboardExpenseTracker
