import React,{useState, useEffect} from 'react'
import {useHistory} from 'react-router-dom'
import axios from 'axios'
import Header from './Components/Header/Header'
import MenuNavActivity from './Components/Menu-Navigation/MenuNavActivity'
import Dashboard from './Components/Dashboard/Dashboard'
import Promotions from './Components/Promotions/Promotions'

function Home(){

    const history = useHistory();
    const[loginAuth,setLoginAuth] = useState("");
    const[userData,setUserData] = useState("");

    useEffect(()=>{
        document.title = "Dashboard · Splitwise"
        const token = localStorage.getItem("login-key");
        // console.log(token)
        axios.post(`http://localhost:4000/check-login`,{"data":token})
          .then(res => {
            //console.log(res);
            var arr = []
            setLoginAuth(res.data[0])
            arr.push(res.data[1][0].id)
            arr.push(res.data[1][0].name)
            setUserData(arr)
        }); 
        if(loginAuth == "jwt expired") {
            localStorage.clear()
            history.push("/loading")
            setTimeout(()=>{
                history.push("/");
            },1700)
        }
        if(loginAuth == "jwt must be provided") {
            localStorage.clear()
            history.push("/loading")
            setTimeout(()=>{
                history.push("/");
            },1700)
        }
    },[])
    return(
        <div>
            <Header user={userData} />
            <div className="main-content">
                <MenuNavActivity />
                <Dashboard user={userData}/>
                <Promotions />
            </div>
        </div>
      )
}

export default Home
