import React from 'react'
import Button from './Button'
import MenuIcon from './menu.png'
import ChartIcon from './stats.png'

function SummaryHeader(props){
    function handleToggle(){
        const updatedToggle = props.viewToggle.map(item => {
            console.log(item)
            if(item.list === false){
                item.list = item.list
            }
        })
        props.handTog(updatedToggle)
    }
    return(
        <div className="summary-header">
            <h2 className="summary-header-h2">you owe<span className="summary-header-span">you are owe</span></h2>
            <div className="toggle-btns-area">
                <Button class="toggle-btns" name="view as list" url={MenuIcon} method={handleToggle}/>  
                <Button class="toggle-btns-2" name="view chart" url={ChartIcon}/>  
            </div>
        </div>
    )
}

export default SummaryHeader
